mapper.select:
select count(1) from wxb_order inner join wxb_memeber on wxb_order.user_id = wxb_memeber.memeber_id inner join wxb_good_sku on wxb_order.sku_id = wxb_good_sku.sku_id inner join wxb_good on wxb_order.good_id = wxb_good.good_id
and wxb_good.customer_id = #{customerId}
and wxb_memeber.memeber_id = #{memeberId}

select count(1) from wxb_order inner join wxb_memeber on wxb_order.user_id = wxb_memeber.memeber_id inner join wxb_good_sku on wxb_order.sku_id = wxb_good_sku.sku_id inner join wxb_good on wxb_order.good_id = wxb_good.good_id
and wxb_good.customer_id = #{customerId}
and wxb_order.state = #{state}

mapper.update:
update wxb_order
set state = #{state}
where order_id = #{orderId}
